function ease(n,t){return n<.5?.5*Math.pow(2*n,t):1-.5*Math.pow(2*(1-n),t)}function easeInOutCubic(n){return n<.5?4*n*n*n:(n-1)*(2*n-2)*(2*n-2)+1}function easeInOutElastic(n){return(n-=.5)<0?(.01+.01/n)*Math.sin(50*n):(.02-.01/n)*Math.sin(50*n)+1}function map_range(n,t,a,e,r){var u=e+(r-e)*((n-t)/(a-t));return u}function getRandomInRange(n,t){return Math.random()*(t-n)+n}function isEven(n){return n%2===0}function isOdd(n){return 1==Math.abs(n%2)}